+  dedupe(names) {
+    const seen = new Set();
+    const unique = [];
+    names.forEach((name) => {
+      if (name && !seen.has(name.toLowerCase())) {
+        seen.add(name.toLowerCase());
+        unique.push(name);
+      }
+    });
+    return unique;
+  },
+  createParticipant(name, index) {
+    const slug = name
+      .toLowerCase()
+      .replace(/[^a-z0-9가-힣]+/g, '-')
+      .replace(/^-+|-+$/g, '') || 'player';
+    return {
+      id: `participant-${index + 1}-${slug}`,
+      name,
+      status: 'active',
+      currentChoice: 'none',
+      placementAngle: 0,
+    };
+  },
+  parse(rawInput = '') {
+    const names = rawInput
+      .split(',')
+      .map((token) => InputParser.sanitize(token))
