    if (participants.length < INPUT_LIMITS.min) {
      warnings.push(`게임을 시작하려면 최소 ${INPUT_LIMITS.min}명이 필요합니다.`);
    }

    return {
      participants,
      count: participants.length,
      isCountValid:
        participants.length >= INPUT_LIMITS.min &&
        participants.length <= INPUT_LIMITS.max,
      warnings,
    };
  },
};

