      .map((choice) => choice.participantId);
    const loserIds = choices
      .filter((choice) => !winnerIds.includes(choice.participantId))
      .map((choice) => choice.participantId);
    return { result: 'resolved', winners: winnerIds, losers: loserIds };
  },
  choiceToEmoji(move) {
