  1326	const ProbabilityCalculator = (() => {
  1327	  const factMemo = [1];
  1328	  const factorial = (num) => {
  1329	    if (factMemo[num] !== undefined) return factMemo[num];
  1330	    let result = factMemo[factMemo.length - 1];
  1331	    for (let i = factMemo.length; i <= num; i++) {
  1332	      result *= i;
  1333	      factMemo[i] = result;
  1334	    }
  1335	    return result;
  1336	  };
  1337	
  1338	  const combinations = (n, k) => {
  1339	    if (k < 0 || k > n) return 0;
  1340	    if (k === 0 || k === n) return 1;
  1341	    if (k > n / 2) k = n - k;
  1342	    return factorial(n) / (factorial(k) * factorial(n - k));
  1343	  };
  1344	
  1345	  const power = (base, exp) => Math.pow(base, exp);
  1346	
  1347	  return {
  1348	    calculate({ numPlayers, winners, losers }) {
  1349	      const n = numPlayers;
  1350	      if (n <= 1) return 0;
  1351	
  1352	      const totalOutcomes = power(3, n);
  1353	      const isStalemate = winners === 0 && losers === 0;
  1354	
  1355	      if (isStalemate) {
  1356	        // All players choose the same (3 ways) OR all 3 moves are present
  1357	        const all3Moves = power(3, n) - 3 * power(2, n) + 3;
  1358	        const favorableOutcomes = 3 + all3Moves;
  1359	        return favorableOutcomes / totalOutcomes;
  1360	      } else {
  1361	        // A decisive outcome where only 2 moves are present
  1362	        const k = winners > 0 ? winners : losers;
  1363	        const favorableOutcomes = 3 * combinations(n, k);
  1364	        return favorableOutcomes / totalOutcomes;
  1365	      }
  1366	    },
  1367	  };
  1368	})();
