  1749	const ProbabilityCalculator = (() => {
  1750	  const factMemo = [1];
  1751	  const factorial = (num) => {
  1752	    if (factMemo[num] !== undefined) return factMemo[num];
  1753	    let result = factMemo[factMemo.length - 1];
  1754	    for (let i = factMemo.length; i <= num; i++) {
  1755	      result *= i;
  1756	      factMemo[i] = result;
  1757	    }
  1758	    return result;
  1759	  };
  1760	
  1761	  const combinations = (n, k) => {
  1762	    if (k < 0 || k > n) return 0;
  1763	    if (k === 0 || k === n) return 1;
  1764	    if (k > n / 2) k = n - k;
  1765	    return factorial(n) / (factorial(k) * factorial(n - k));
  1766	  };
  1767	
  1768	  const power = (base, exp) => Math.pow(base, exp);
